// Default brick data.new ScriptObject(Brick) {		// Model & shape	model      = "art/shapes/bricks/1x1.dts"; // Shape model	ghostmodel = "art/shapes/bricks/1x1.dts"; // Ghost brick model, leave blank if same as above.	brickScale = "1 1 1"; // How much to scale original brick.};function Brick::createTSStatic(%this) {	return new TSStatic() {		shapeName = %this.model;		scale     = %this.brickScale;	};}function Brick::serialise(%this) {	return (		%model TAB		%brickScale	);}function Brick::deserialise(%this, %data) {	// not implemented}